package Without_Inheritance;

public class Student {
	int id;
	String name;
	int marks;
	
	void setId(int id)
	{
		this.id=id;
	}

	int getId()
	{
		return id;
	}
	
	void setName(String name)
	{
		this.name=name;
	}
	
	String getName()
	{
		return name;
	}

	void setMarks(int marks)
	{
		this.marks=marks;
	}
	
	int getMarks()
	{
		return marks;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	}

}
